import { getEntry } from 'astro:content';

const post = await getEntry('blog', Astro.params.slug);
const { Content, headings } = await post.render();
---

<article>
  <h1>{post.data.title}</h1>
  <Content />  <!-- Rendered markdown -->
</article>

