import { S3Client, PutObjectCommand } from '@aws-sdk/client-s3';
const client = new S3Client({ region: 'us-east-1' });
await client.send(new PutObjectCommand({
  Bucket: 'my-bucket',
  Key: 'file.txt',
  Body: 'Hello World'
}));

import { GetObjectCommand } from '@aws-sdk/client-s3';
const response = await client.send(new GetObjectCommand({
  Bucket: 'my-bucket',
  Key: 'file.txt'
}));
const body = await response.Body.transformToString();

import { ListObjectsV2Command } from '@aws-sdk/client-s3';
const response = await client.send(new ListObjectsV2Command({
  Bucket: 'my-bucket',
  Prefix: 'folder/'
}));
const files = response.Contents;

import { getSignedUrl } from '@aws-sdk/s3-request-presigner';
const url = await getSignedUrl(client, new GetObjectCommand({
  Bucket: 'my-bucket',
  Key: 'file.txt'
}), { expiresIn: 3600 });

import { DynamoDBClient, PutItemCommand } from '@aws-sdk/client-dynamodb';
const client = new DynamoDBClient({ region: 'us-east-1' });
await client.send(new PutItemCommand({
  TableName: 'Users',
  Item: {
    pk: { S: 'user#123' },
    name: { S: 'John' }
  }
}));

import { GetItemCommand } from '@aws-sdk/client-dynamodb';
const response = await client.send(new GetItemCommand({
  TableName: 'Users',
  Key: { pk: { S: 'user#123' } }
}));
const item = response.Item;

import { QueryCommand } from '@aws-sdk/client-dynamodb';
const response = await client.send(new QueryCommand({
  TableName: 'Users',
  KeyConditionExpression: 'pk = :pk',
  ExpressionAttributeValues: { ':pk': { S: 'user#123' } }
}));

import { DynamoDBDocumentClient, PutCommand } from '@aws-sdk/lib-dynamodb';
const docClient = DynamoDBDocumentClient.from(client);
await docClient.send(new PutCommand({
  TableName: 'Users',
  Item: { pk: 'user#123', name: 'John' }
}));

import { LambdaClient, InvokeCommand } from '@aws-sdk/client-lambda';
const client = new LambdaClient({ region: 'us-east-1' });
const response = await client.send(new InvokeCommand({
  FunctionName: 'my-function',
  Payload: JSON.stringify({ key: 'value' })
}));

export const handler = async (event, context) => {
  console.log('Event:', JSON.stringify(event));
  return {
    statusCode: 200,
    body: JSON.stringify({ message: 'Success' })
  };
};

import { SQSClient, SendMessageCommand } from '@aws-sdk/client-sqs';
const client = new SQSClient({ region: 'us-east-1' });
await client.send(new SendMessageCommand({
  QueueUrl: 'https://sqs.us-east-1.amazonaws.com/123456789/my-queue',
  MessageBody: JSON.stringify({ task: 'process' })
}));

import { ReceiveMessageCommand, DeleteMessageCommand } from '@aws-sdk/client-sqs';
const response = await client.send(new ReceiveMessageCommand({
  QueueUrl: queueUrl,
  MaxNumberOfMessages: 10,
  WaitTimeSeconds: 20
}));
for (const msg of response.Messages || []) {
  // Process message
  await client.send(new DeleteMessageCommand({ QueueUrl: queueUrl, ReceiptHandle: msg.ReceiptHandle }));
}

import { SNSClient, PublishCommand } from '@aws-sdk/client-sns';
const client = new SNSClient({ region: 'us-east-1' });
await client.send(new PublishCommand({
  TopicArn: 'arn:aws:sns:us-east-1:123456789:my-topic',
  Message: JSON.stringify({ event: 'user.created' })
}));

import { SecretsManagerClient, GetSecretValueCommand } from '@aws-sdk/client-secrets-manager';
const client = new SecretsManagerClient({ region: 'us-east-1' });
const response = await client.send(new GetSecretValueCommand({ SecretId: 'my-secret' }));
const secret = JSON.parse(response.SecretString);

import { SSMClient, GetParameterCommand } from '@aws-sdk/client-ssm';
const client = new SSMClient({ region: 'us-east-1' });
const response = await client.send(new GetParameterCommand({
  Name: '/app/config/api-key',
  WithDecryption: true
}));
const value = response.Parameter.Value;

import { CognitoIdentityProviderClient, InitiateAuthCommand } from '@aws-sdk/client-cognito-identity-provider';
const response = await client.send(new InitiateAuthCommand({
  AuthFlow: 'USER_PASSWORD_AUTH',
  ClientId: 'client-id',
  AuthParameters: { USERNAME: 'user', PASSWORD: 'pass' }
}));

import { SESClient, SendEmailCommand } from '@aws-sdk/client-ses';
await client.send(new SendEmailCommand({
  Source: 'from@example.com',
  Destination: { ToAddresses: ['to@example.com'] },
  Message: {
    Subject: { Data: 'Hello' },
    Body: { Text: { Data: 'Message body' } }
  }
}));

import { CloudWatchLogsClient, PutLogEventsCommand } from '@aws-sdk/client-cloudwatch-logs';
await client.send(new PutLogEventsCommand({
  logGroupName: '/app/logs',
  logStreamName: 'stream-1',
  logEvents: [{ timestamp: Date.now(), message: 'Log entry' }]
}));

import { EC2Client, DescribeInstancesCommand } from '@aws-sdk/client-ec2';
const response = await client.send(new DescribeInstancesCommand({
  Filters: [{ Name: 'instance-state-name', Values: ['running'] }]
}));
const instances = response.Reservations.flatMap(r => r.Instances);

import { RDSClient, DescribeDBInstancesCommand } from '@aws-sdk/client-rds';
const response = await client.send(new DescribeDBInstancesCommand({}));
// Use endpoint for database connection
const endpoint = response.DBInstances[0].Endpoint.Address;

import { STSClient, AssumeRoleCommand } from '@aws-sdk/client-sts';
const response = await client.send(new AssumeRoleCommand({
  RoleArn: 'arn:aws:iam::123456789:role/my-role',
  RoleSessionName: 'session-1'
}));
const credentials = response.Credentials;

import { EventBridgeClient, PutEventsCommand } from '@aws-sdk/client-eventbridge';
await client.send(new PutEventsCommand({
  Entries: [{
    Source: 'my.app',
    DetailType: 'UserCreated',
    Detail: JSON.stringify({ userId: '123' })
  }]
}));

import { SFNClient, StartExecutionCommand } from '@aws-sdk/client-sfn';
await client.send(new StartExecutionCommand({
  stateMachineArn: 'arn:aws:states:us-east-1:123456789:stateMachine:my-workflow',
  input: JSON.stringify({ orderId: '123' })
}));

import { BedrockRuntimeClient, InvokeModelCommand } from '@aws-sdk/client-bedrock-runtime';
const response = await client.send(new InvokeModelCommand({
  modelId: 'anthropic.claude-v2',
  body: JSON.stringify({ prompt: 'Hello' })
}));

import { KinesisClient, PutRecordCommand } from '@aws-sdk/client-kinesis';
await client.send(new PutRecordCommand({
  StreamName: 'my-stream',
  Data: Buffer.from(JSON.stringify({ event: 'click' })),
  PartitionKey: 'user-123'
}));

import { ApiGatewayManagementApiClient, PostToConnectionCommand } from '@aws-sdk/client-apigatewaymanagementapi';
const client = new ApiGatewayManagementApiClient({ endpoint: 'https://xxx.execute-api.us-east-1.amazonaws.com/prod' });
await client.send(new PostToConnectionCommand({
  ConnectionId: 'abc123',
  Data: JSON.stringify({ message: 'Hello' })
}));

import { AthenaClient, StartQueryExecutionCommand } from '@aws-sdk/client-athena';
const response = await client.send(new StartQueryExecutionCommand({
  QueryString: 'SELECT * FROM my_table LIMIT 10',
  ResultConfiguration: { OutputLocation: 's3://results-bucket/' }
}));

client.middlewareStack.add(
  (next) => async (args) => {
    console.log('Request:', args.request);
    const result = await next(args);
    console.log('Response:', result.response);
    return result;
  },
  { step: 'build' }
);
