skills.pattern:
import { pgTable, serial, text, timestamp } from 'drizzle-orm/pg-core';
export const users = pgTable('users', {
  id: serial('id').primaryKey(),
  name: text('name').notNull(),
  createdAt: timestamp('created_at').defaultNow()
});

import { mysqlTable, int, varchar, timestamp } from 'drizzle-orm/mysql-core';
export const users = mysqlTable('users', {
  id: int('id').primaryKey().autoincrement(),
  name: varchar('name', { length: 255 })
});

import { sqliteTable, integer, text } from 'drizzle-orm/sqlite-core';
export const users = sqliteTable('users', {
  id: integer('id').primaryKey(),
  name: text('name').notNull()
});

import { drizzle } from 'drizzle-orm/node-postgres';
import { Pool } from 'pg';
const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle(pool);

import { neon } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-http';
const sql = neon(process.env.DATABASE_URL!);
export const db = drizzle(sql);

await db.insert(users).values({ name: 'John', email: 'john@example.com' });
// Insert many
await db.insert(users).values([{ name: 'A' }, { name: 'B' }]);

await db.select().from(users)
  .where(and(
    eq(users.active, true),
    or(gt(users.age, 18), eq(users.role, 'admin'))
  ));

import { relations } from 'drizzle-orm';
export const usersRelations = relations(users, ({ many }) => ({
  posts: many(posts)
}));
export const postsRelations = relations(posts, ({ one }) => ({
  author: one(users, { fields: [posts.authorId], references: [users.id] })
}));

const result = await db.query.users.findMany({
  with: { posts: true }
});

import { count, sum, avg, max, min } from 'drizzle-orm';
await db.select({ total: count() }).from(users);
await db.select({ avgAge: avg(users.age) }).from(users);

await db.select({
  department: users.department,
  count: count()
}).from(users).groupBy(users.department);

await db.transaction(async (tx) => {
  await tx.insert(users).values({ name: 'John' });
  await tx.insert(posts).values({ title: 'Hello' });
});

import { sql } from 'drizzle-orm';
await db.execute(sql`SELECT * FROM users WHERE age > ${18}`);
const users = await db.select().from(usersTable).where(sql`age > 18`);

import type { Config } from 'drizzle-kit';
export default {
  schema: './src/db/schema.ts',
  out: './drizzle',
  driver: 'pg',
  dbCredentials: { connectionString: process.env.DATABASE_URL! }
} satisfies Config;
