skills.pattern:
app.get('/', ({ set }) => {
  set.status = 201;
  set.headers['X-Custom'] = 'value';
  return { created: true };
});

import { t } from 'elysia';
app.post('/user', ({ body }) => body, {
  body: t.Object({ name: t.String(), age: t.Number() })
});

app.get('/user', () => ({ name: 'John' }), {
  response: t.Object({ name: t.String() })
});

app.get('/search', ({ query }) => query, {
  query: t.Object({ q: t.String(), page: t.Optional(t.Numeric()) })
});

import { jwt } from '@elysiajs/jwt';
app.use(jwt({ name: 'jwt', secret: 'secret' }))
   .get('/sign', ({ jwt }) => jwt.sign({ sub: '1' }));

app.derive(({ headers }) => ({
  user: getUserFromHeader(headers.authorization)
}));

app.onError(({ code, error }) => {
  if (code === 'NOT_FOUND') return 'Not Found';
  return error.message;
});

app.get('/cookie', ({ cookie: { name } }) => {
  name.set({ value: 'value', httpOnly: true });
  return name.value;
});

app.group('/api', app => app
  .get('/users', () => 'Users')
  .get('/posts', () => 'Posts')
);

app.guard({ beforeHandle: () => { /* auth */ } }, app => app
  .get('/protected', () => 'Secret')
);

app.ws('/ws', {
  message(ws, message) { ws.send(message) },
  open(ws) { console.log('Connected') }
});

import { edenTreaty } from '@elysiajs/eden';
const api = edenTreaty<App>('http://localhost:3000');
const { data } = await api.users.get();
