import * as esbuild from 'esbuild';

await esbuild.build({
  entryPoints: ['src/index.ts'],
  bundle: true,
  outfile: 'dist/bundle.js'
});

const result = await esbuild.transform(code, {
  loader: 'tsx',
  minify: true
});
console.log(result.code);

await esbuild.build({
  entryPoints: ['src/index.ts'],
  bundle: true,
  minify: true,
  sourcemap: true,
  target: ['es2020'],
  outdir: 'dist'
});

format: 'esm',  // ES modules
// format: 'cjs',  // CommonJS
// format: 'iife',  // Browser

loader: {
  '.png': 'file',
  '.svg': 'text',
  '.json': 'json'
}

define: {
  'process.env.NODE_ENV': '"production"',
  '__VERSION__': '"1.0.0"'
}

alias: {
  '@': './src',
  '@components': './src/components'
}

const ctx = await esbuild.context(options);
await ctx.watch();
console.log('Watching...');

const ctx = await esbuild.context(options);
const { host, port } = await ctx.serve({
  servedir: 'dist',
  port: 3000
});

const plugin = {
  name: 'my-plugin',
  setup(build) {
    build.onResolve({ filter: /^env$/ }, () => ({ path: 'env', namespace: 'env' }));
    build.onLoad({ filter: /.*/, namespace: 'env' }, () => ({ contents: JSON.stringify(process.env) }));
  }
};

await esbuild.build({
  entryPoints: ['src/styles.css'],
  bundle: true,
  minify: true,
  outfile: 'dist/styles.css'
});

await esbuild.build({
  entryPoints: ['src/main.ts', 'src/admin.ts'],
  splitting: true,
  format: 'esm',
  outdir: 'dist'
});

sourcemap: true,  // External
// sourcemap: 'inline',  // Inline
// sourcemap: 'linked'  // Reference

banner: {
  js: '// Copyright 2024',
  css: '/* My Styles */'
},
footer: { js: '// End' }

legalComments: 'linked',  // Separate file
// legalComments: 'inline',  // Keep
// legalComments: 'none'  // Remove

stdin: {
  contents: 'export const answer = 42',
  resolveDir: process.cwd(),
  sourcefile: 'virtual.ts',
  loader: 'ts'
}

const result = await esbuild.build({
  write: false  // Don't write to disk
});
console.log(result.outputFiles);

const ctx = await esbuild.context(options);
await ctx.rebuild();
await ctx.rebuild();  // Fast
await ctx.dispose();

// esbuild is 10-100x faster than alternatives
// Written in Go, uses parallel processing
// Great for large codebases
