skills.pattern:
// pages/index.vue - Home (/)
// pages/about.vue - About (/about)
// pages/blog/[slug].vue - Dynamic (/blog/hello)
// pages/[...slug].vue - Catch-all

// server/api/users.ts
export default defineEventHandler(async (event) => {
  return await db.users.findMany();
});

// server/api/users/[id].ts
export default defineEventHandler((event) => {
  const id = getRouterParam(event, 'id');
  return db.users.findById(id);
});

// stores/counter.ts
export const useCounterStore = defineStore('counter', {
  state: () => ({ count: 0 }),
  actions: {
    increment() { this.count++; }
  }
});

// In component
const counter = useCounterStore();

// middleware/auth.ts
export default defineNuxtRouteMiddleware((to, from) => {
  const user = useUser();
  if (!user.value) return navigateTo('/login');
});

// In page
<script setup>
definePageMeta({ middleware: 'auth' });
</script>

// plugins/my-plugin.ts
export default defineNuxtPlugin((nuxtApp) => {
  return {
    provide: {
      hello: (msg) => `Hello ${msg}!`
    }
  };
});

// Use
const { $hello } = useNuxtApp();

// composables/useCounter.ts
export const useCounter = () => {
  const count = useState('count', () => 0);
  const increment = () => count.value++;
  return { count, increment };
};

export default defineNuxtConfig({
  devtools: { enabled: true },
  modules: ['@pinia/nuxt', '@nuxtjs/tailwindcss'],
  runtimeConfig: {
    apiSecret: '',
    public: {
      apiBase: '/api'
    }
  }
});

// nuxt.config.ts
runtimeConfig: {
  apiSecret: process.env.API_SECRET,
  public: { apiBase: '/api' }
}

// Access
const config = useRuntimeConfig();
config.public.apiBase;  // Client OK
config.apiSecret;       // Server only

// nuxt.config.ts
export default defineNuxtConfig({
  ssr: true,  // Server-side rendering
  // ssr: false,  // SPA mode

  // Or per-route
  routeRules: {
    '/': { prerender: true },
    '/api/**': { cors: true },
    '/admin/**': { ssr: false }
  }
});

// server/api/user.post.ts
export default defineEventHandler(async (event) => {
  const body = await readBody(event);
  const query = getQuery(event);
  const headers = getHeaders(event);

  setCookie(event, 'token', 'value');
  return { success: true };
});

// server/middleware/log.ts
export default defineEventHandler((event) => {
  console.log('Request:', event.path);
});

// server/plugins/db.ts
export default defineNitroPlugin((nitroApp) => {
  console.log('Nitro started');
});

// nuxt.config.ts
app: {
  pageTransition: { name: 'page', mode: 'out-in' }
}

/* CSS */
.page-enter-active, .page-leave-active {
  transition: all 0.3s;
}
.page-enter-from, .page-leave-to {
  opacity: 0;
}

// vitest.config.ts
import { defineVitestConfig } from '@nuxt/test-utils/config';

export default defineVitestConfig({
  test: {
    environment: 'nuxt'
  }
});

// modules/my-module.ts
export default defineNuxtModule({
  meta: { name: 'my-module' },
  setup(options, nuxt) {
    addPlugin({ src: resolve('./runtime/plugin') });
  }
});
