module.exports = {
  plugins: [
    require('autoprefixer'),
    require('cssnano')
  ]
}

npm install postcss-mixins
@define-mixin flex-center { display: flex; justify-content: center; }
.box { @mixin flex-center; }

npm install postcss-simple-vars
$color: blue;
.btn { color: $color; }

// Transforms CSS custom properties for older browsers
:root { --color: blue; }
.btn { color: var(--color); }

npm install postcss-calc
// Reduces calc() when possible
width: calc(100% / 3); // compiles to 33.333%
