import styled from 'styled-components';

const Button = styled.button`
  background: blue;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
`;

export default Button;

const Button = styled.button<{ primary?: boolean }>`
  background: ${props => props.primary ? 'blue' : 'gray'};
  color: white;
  padding: ${props => props.primary ? '12px 24px' : '8px 16px'};
`;

<Button primary>Primary</Button>

const Button = styled.button`
  color: white;
  padding: 10px;
`;

const PrimaryButton = styled(Button)`
  background: blue;
`;

const DangerButton = styled(Button)`
  background: red;
`;

const Input = styled.input.attrs(props => ({
  type: 'text',
  placeholder: props.placeholder || 'Enter text'
}))`
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Button = styled.button`
  background: blue;

  &:hover {
    background: darkblue;
  }

  &:active {
    transform: scale(0.98);
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const Card = styled.div`
  padding: 20px;

  h2 {
    margin-bottom: 10px;
  }

  p {
    color: gray;
  }

  &.active {
    border: 2px solid blue;
  }
`;

import { ThemeProvider } from 'styled-components';

const theme = {
  colors: {
    primary: '#007bff',
    secondary: '#6c757d'
  },
  spacing: {
    small: '8px',
    medium: '16px'
  }
};

<ThemeProvider theme={theme}>
  <App />
</ThemeProvider>

const Button = styled.button`
  background: ${props => props.theme.colors.primary};
  padding: ${props => props.theme.spacing.medium};
`;

// Or with hook
import { useTheme } from 'styled-components';
const theme = useTheme();

import { createGlobalStyle } from 'styled-components';

const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  body {
    font-family: 'Inter', sans-serif;
  }
`;

<GlobalStyle />

import styled, { keyframes } from 'styled-components';

const rotate = keyframes`
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
`;

const Spinner = styled.div`
  animation: ${rotate} 1s linear infinite;
  width: 40px;
  height: 40px;
  border: 4px solid #ccc;
  border-top-color: blue;
  border-radius: 50%;
`;

import styled, { css } from 'styled-components';

const flexCenter = css`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Box = styled.div`
  ${flexCenter}
  width: 100px;
  height: 100px;
`;

const Container = styled.div`
  width: 100%;
  padding: 20px;

  @media (min-width: 768px) {
    width: 750px;
    margin: 0 auto;
  }

  @media (min-width: 1024px) {
    width: 970px;
  }
`;

const Button = styled.button`
  padding: 10px 20px;
  background: blue;
  color: white;
`;

// Render as different element
<Button as="a" href="/link">Link</Button>
<Button as="div">Div</Button>

import styled from 'styled-components';
import isPropValid from '@emotion/is-prop-valid';

const Box = styled('div').withConfig({
  shouldForwardProp: (prop) => isPropValid(prop) && prop !== 'color'
})<{ color: string }>`
  background: ${props => props.color};
`;

const Input = styled.input`
  padding: 10px;
`;

function Form() {
  const inputRef = useRef<HTMLInputElement>(null);

  return <Input ref={inputRef} />;
}

const Button = styled.button<{ $primary?: boolean }>`
  background: ${props => props.$primary ? 'blue' : 'gray'};
`;

// $primary won't be passed to DOM
<Button $primary>Click</Button>

import { ServerStyleSheet } from 'styled-components';

// Next.js _document.tsx
static async getInitialProps(ctx) {
  const sheet = new ServerStyleSheet();
  const originalRenderPage = ctx.renderPage;

  ctx.renderPage = () => originalRenderPage({
    enhanceApp: (App) => (props) => sheet.collectStyles(<App {...props} />)
  });

  const styles = sheet.getStyleElement();
  return { ...initialProps, styles };
}

// babel.config.js
module.exports = {
  plugins: [
    ['babel-plugin-styled-components', {
      displayName: true,
      fileName: true,
      meaninglessFileNames: ['index', 'styles']
    }]
  ]
};

// styled.d.ts
import 'styled-components';

declare module 'styled-components' {
  export interface DefaultTheme {
    colors: {
      primary: string;
      secondary: string;
    };
    spacing: {
      small: string;
      medium: string;
    };
  }
}

const Icon = styled.span`
  font-size: 20px;
`;

const Button = styled.button`
  display: flex;
  align-items: center;

  ${Icon} {
    margin-right: 8px;
  }

  &:hover ${Icon} {
    color: blue;
  }
`;

const getButtonStyles = ({ size, variant }) => css`
  padding: ${size === 'large' ? '16px 32px' : '8px 16px'};
  background: ${variant === 'primary' ? 'blue' : 'gray'};
`;

const Button = styled.button`
  ${props => getButtonStyles(props)}
`;

const buttonVariants = {
  primary: css`background: blue; color: white;`,
  secondary: css`background: gray; color: black;`,
  danger: css`background: red; color: white;`
};

const Button = styled.button<{ variant: keyof typeof buttonVariants }>`
  padding: 10px 20px;
  ${props => buttonVariants[props.variant]}
`;

const truncate = css`
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const shadow = (size: 'sm' | 'md' | 'lg') => css`
  box-shadow: ${size === 'sm' ? '0 1px 2px' : size === 'md' ? '0 4px 6px' : '0 10px 15px'} rgba(0,0,0,0.1);
`;

const Button = styled.button(props => ({
  backgroundColor: props.primary ? 'blue' : 'gray',
  color: 'white',
  padding: '10px 20px',
  border: 'none',
  '&:hover': {
    opacity: 0.8
  }
}));

import { render } from '@testing-library/react';
import 'jest-styled-components';

test('Button has correct styles', () => {
  const { container } = render(<Button primary />);
  expect(container.firstChild).toHaveStyleRule('background', 'blue');
});

const spacing = {
  xs: '4px',
  sm: '8px',
  md: '16px',
  lg: '24px',
  xl: '32px'
};

const Box = styled.div<{ p?: keyof typeof spacing; m?: keyof typeof spacing }>`
  padding: ${props => spacing[props.p || 'md']};
  margin: ${props => spacing[props.m || 'sm']};
`;

const Grid = styled.div<{ columns?: number; gap?: string }>`
  display: grid;
  grid-template-columns: repeat(${props => props.columns || 3}, 1fr);
  gap: ${props => props.gap || '16px'};
`;

<Grid columns={4} gap="20px">
  <div>Item 1</div>
  <div>Item 2</div>
</Grid>

const Flex = styled.div<{
  direction?: 'row' | 'column';
  justify?: string;
  align?: string;
  gap?: string;
}>`
  display: flex;
  flex-direction: ${props => props.direction || 'row'};
  justify-content: ${props => props.justify || 'flex-start'};
  align-items: ${props => props.align || 'stretch'};
  gap: ${props => props.gap || '0'};
`;

// 1. Use transient props ($prop) to prevent DOM forwarding
// 2. Define theme types for TypeScript
// 3. Use css helper for shared styles
// 4. Keep styled components in separate files
// 5. Use meaningful component names
