// Or in code
import { S3Client } from '@aws-sdk/client-s3';
const client = new S3Client({ region: 'us-east-1' });

import { S3Client, PutObjectCommand } from '@aws-sdk/client-s3';

const s3 = new S3Client({ region: 'us-east-1' });

await s3.send(new PutObjectCommand({
  Bucket: 'mybucket',
  Key: 'file.txt',
  Body: Buffer.from('Hello')
}));

import { GetObjectCommand } from '@aws-sdk/client-s3';

const { Body } = await s3.send(new GetObjectCommand({
  Bucket: 'mybucket',
  Key: 'file.txt'
}));

const content = await Body.transformToString();

import { getSignedUrl } from '@aws-sdk/s3-request-presigner';

const url = await getSignedUrl(s3, new GetObjectCommand({
  Bucket: 'mybucket',
  Key: 'file.txt'
}), { expiresIn: 3600 });

import { DynamoDBClient, PutItemCommand } from '@aws-sdk/client-dynamodb';

const ddb = new DynamoDBClient({ region: 'us-east-1' });

await ddb.send(new PutItemCommand({
  TableName: 'users',
  Item: {
    id: { S: '123' },
    name: { S: 'John' }
  }
}));

import { GetItemCommand } from '@aws-sdk/client-dynamodb';

const { Item } = await ddb.send(new GetItemCommand({
  TableName: 'users',
  Key: { id: { S: '123' } }
}));

import { QueryCommand } from '@aws-sdk/client-dynamodb';

const { Items } = await ddb.send(new QueryCommand({
  TableName: 'users',
  KeyConditionExpression: 'pk = :pk',
  ExpressionAttributeValues: { ':pk': { S: 'USER' } }
}));

import { LambdaClient, InvokeCommand } from '@aws-sdk/client-lambda';

const lambda = new LambdaClient({ region: 'us-east-1' });

const { Payload } = await lambda.send(new InvokeCommand({
  FunctionName: 'myfunction',
  Payload: JSON.stringify({ action: 'process' })
}));

import { SQSClient, SendMessageCommand } from '@aws-sdk/client-sqs';

const sqs = new SQSClient({ region: 'us-east-1' });

await sqs.send(new SendMessageCommand({
  QueueUrl: 'https://sqs.us-east-1.amazonaws.com/123456789/myqueue',
  MessageBody: JSON.stringify({ task: 'process' })
}));

import { ReceiveMessageCommand, DeleteMessageCommand } from '@aws-sdk/client-sqs';

const { Messages } = await sqs.send(new ReceiveMessageCommand({
  QueueUrl: 'https://...',
  MaxNumberOfMessages: 10
}));

for (const msg of Messages || []) {
  await processMessage(msg);
  await sqs.send(new DeleteMessageCommand({ QueueUrl: '...', ReceiptHandle: msg.ReceiptHandle }));
}

import { SNSClient, PublishCommand } from '@aws-sdk/client-sns';

const sns = new SNSClient({ region: 'us-east-1' });

await sns.send(new PublishCommand({
  TopicArn: 'arn:aws:sns:us-east-1:123456789:mytopic',
  Message: JSON.stringify({ alert: 'Error occurred' }),
  Subject: 'Alert'
}));

import { SecretsManagerClient, GetSecretValueCommand } from '@aws-sdk/client-secrets-manager';

const sm = new SecretsManagerClient({ region: 'us-east-1' });

const { SecretString } = await sm.send(new GetSecretValueCommand({ SecretId: 'mysecret' }));
const secret = JSON.parse(SecretString);

import { SSMClient, GetParameterCommand } from '@aws-sdk/client-ssm';

const ssm = new SSMClient({ region: 'us-east-1' });

const { Parameter } = await ssm.send(new GetParameterCommand({
  Name: '/myapp/dburl',
  WithDecryption: true
}));

import { SESClient, SendEmailCommand } from '@aws-sdk/client-ses';

const ses = new SESClient({ region: 'us-east-1' });

await ses.send(new SendEmailCommand({
  Source: 'from@example.com',
  Destination: { ToAddresses: ['to@example.com'] },
  Message: {
    Subject: { Data: 'Hello' },
    Body: { Text: { Data: 'Content' } }
  }
}));

import { CognitoIdentityProviderClient, InitiateAuthCommand } from '@aws-sdk/client-cognito-identity-provider';

const cognito = new CognitoIdentityProviderClient({ region: 'us-east-1' });

const { AuthenticationResult } = await cognito.send(new InitiateAuthCommand({
  AuthFlow: 'USER_PASSWORD_AUTH',
  ClientId: 'clientid',
  AuthParameters: { USERNAME: 'user', PASSWORD: 'pass' }
}));

import { CloudWatchClient, PutMetricDataCommand } from '@aws-sdk/client-cloudwatch';

const cw = new CloudWatchClient({ region: 'us-east-1' });

await cw.send(new PutMetricDataCommand({
  Namespace: 'MyApp',
  MetricData: [{ MetricName: 'RequestCount', Value: 1, Unit: 'Count' }]
}));

import { EventBridgeClient, PutEventsCommand } from '@aws-sdk/client-eventbridge';

const eb = new EventBridgeClient({ region: 'us-east-1' });

await eb.send(new PutEventsCommand({
  Entries: [{
    Source: 'myapp',
    DetailType: 'OrderCreated',
    Detail: JSON.stringify({ orderId: '123' })
  }]
}));

import { SFNClient, StartExecutionCommand } from '@aws-sdk/client-sfn';

const sfn = new SFNClient({ region: 'us-east-1' });

await sfn.send(new StartExecutionCommand({
  stateMachineArn: 'arn:aws:states:...',
  input: JSON.stringify({ orderId: '123' })
}));

import { EC2Client, DescribeInstancesCommand } from '@aws-sdk/client-ec2';

const ec2 = new EC2Client({ region: 'us-east-1' });

const { Reservations } = await ec2.send(new DescribeInstancesCommand({}));
const instances = Reservations.flatMap(r => r.Instances);

import { RDSClient, DescribeDBInstancesCommand } from '@aws-sdk/client-rds';

const rds = new RDSClient({ region: 'us-east-1' });

const { DBInstances } = await rds.send(new DescribeDBInstancesCommand({}));

import { ECSClient, ListServicesCommand } from '@aws-sdk/client-ecs';

const ecs = new ECSClient({ region: 'us-east-1' });

const { serviceArns } = await ecs.send(new ListServicesCommand({ cluster: 'mycluster' }));

import { ECRClient, GetAuthorizationTokenCommand } from '@aws-sdk/client-ecr';

const ecr = new ECRClient({ region: 'us-east-1' });

const { authorizationData } = await ecr.send(new GetAuthorizationTokenCommand({}));
const token = Buffer.from(authorizationData[0].authorizationToken, 'base64').toString();

import { CloudFrontClient, CreateInvalidationCommand } from '@aws-sdk/client-cloudfront';

const cf = new CloudFrontClient({ region: 'us-east-1' });

await cf.send(new CreateInvalidationCommand({
  DistributionId: 'EDFDVBD632BHDS5',
  InvalidationBatch: {
    CallerReference: Date.now().toString(),
    Paths: { Quantity: 1, Items: ['/*'] }
  }
}));

import { Route53Client, ListHostedZonesCommand } from '@aws-sdk/client-route-53';

const r53 = new Route53Client({ region: 'us-east-1' });

const { HostedZones } = await r53.send(new ListHostedZonesCommand({}));

import { IAMClient, ListUsersCommand } from '@aws-sdk/client-iam';

const iam = new IAMClient({ region: 'us-east-1' });

const { Users } = await iam.send(new ListUsersCommand({}));

import { KinesisClient, PutRecordCommand } from '@aws-sdk/client-kinesis';

const kinesis = new KinesisClient({ region: 'us-east-1' });

await kinesis.send(new PutRecordCommand({
  StreamName: 'mystream',
  Data: Buffer.from(JSON.stringify({ event: 'click' })),
  PartitionKey: 'user123'
}));

import { TranscribeClient, StartTranscriptionJobCommand } from '@aws-sdk/client-transcribe';

const transcribe = new TranscribeClient({ region: 'us-east-1' });

await transcribe.send(new StartTranscriptionJobCommand({
  TranscriptionJobName: 'myjob',
  LanguageCode: 'en-US',
  Media: { MediaFileUri: 's3://bucket/audio.mp3' }
}));

import { TextractClient, AnalyzeDocumentCommand } from '@aws-sdk/client-textract';

const textract = new TextractClient({ region: 'us-east-1' });

const { Blocks } = await textract.send(new AnalyzeDocumentCommand({
  Document: { S3Object: { Bucket: 'bucket', Name: 'doc.pdf' } },
  FeatureTypes: ['TABLES', 'FORMS']
}));

