// In test
const apiUrl = Cypress.env('apiUrl');

// cypress.config.ts
setupNodeEvents(on) {
  on('task', {
    seedDb() { return db.seed(); }
  });
}

// In test
cy.task('seedDb');

cy.get('table tr').as('rows');
cy.get('@rows').should('have.length', 5);

cy.intercept('GET', '/api/users').as('getUsers');
cy.wait('@getUsers');

cy.get('[name=email]').type('user@example.com');
cy.get('[name=password]').type('password');
cy.get('select[name=country]').select('USA');
cy.get('[type=radio][value=yes]').check();
cy.get('form').submit();

cy.get('[type=file]').selectFile('cypress/fixtures/image.png');

cy.get('.dropzone').selectFile('file.pdf', {
  action: 'drag-drop'
});

cy.get('my-component')
  .shadow()
  .find('button')
  .click();

cy.get('my-component button', { includeShadowDom: true }).click();

cy.clock(new Date('2024-01-01').getTime());
cy.visit('/timer');
cy.tick(5000);  // Advance 5 seconds
cy.get('.countdown').should('have.text', '55');

cy.window().then((win) => {
  cy.stub(win.console, 'log').as('consoleLog');
});

cy.get('button').click();
cy.get('@consoleLog').should('be.calledWith', 'clicked');

