import { defineConfig } from 'cypress';

export default defineConfig({
  e2e: {
    baseUrl: 'http://localhost:3000',
    supportFile: 'cypress/support/e2e.ts',
    specPattern: 'cypress/e2e/**/*.cy.{js,ts}'
  },
  component: {
    devServer: {
      framework: 'react',
      bundler: 'vite'
    }
  }
});

describe('Home Page', () => {
  it('displays welcome message', () => {
    cy.visit('/');
    cy.contains('Welcome').should('be.visible');
  });
});

cy.get('button').click();
cy.get('input').type('Hello');
cy.get('input').clear();
cy.get('select').select('option1');
cy.get('checkbox').check();
cy.get('checkbox').uncheck();
cy.get('form').submit();
cy.get('a').dblclick();
cy.get('div').trigger('mouseover');

cy.get('h1').should('be.visible');
cy.get('h1').should('have.text', 'Title');
cy.get('h1').should('contain', 'Tit');
cy.get('input').should('have.value', 'test');
cy.get('.item').should('have.length', 3);
cy.get('button').should('be.disabled');
cy.get('div').should('have.class', 'active');
cy.url().should('include', '/dashboard');

cy.get('table').find('tr').as('rows');

// Use later
cy.get('@rows').should('have.length', 5);
cy.get('@rows').first().click();

// With intercept
cy.intercept('/api/users').as('getUsers');
cy.wait('@getUsers');

// Mock response
cy.intercept('GET', '/api/users', {
  statusCode: 200,
  body: [{ id: 1, name: 'John' }]
}).as('getUsers');

// Fixture file
cy.intercept('GET', '/api/users', { fixture: 'users.json' });

// Modify request
cy.intercept('POST', '/api/*', (req) => {
  req.headers['X-Custom'] = 'value';
});

// Wait for request
cy.intercept('/api/data').as('getData');
cy.wait('@getData');

// Wait with timeout
cy.get('.loading', { timeout: 10000 }).should('not.exist');

// Wait for element
cy.get('.results').should('be.visible');

// cypress/fixtures/users.json
[{ "id": 1, "name": "John" }]

// Use in test
cy.fixture('users.json').then((users) => {
  cy.get('input').type(users[0].name);
});

// Or with intercept
cy.intercept('GET', '/api/users', { fixture: 'users.json' });

// cypress/support/commands.ts
Cypress.Commands.add('login', (email, password) => {
  cy.visit('/login');
  cy.get('[data-cy=email]').type(email);
  cy.get('[data-cy=password]').type(password);
  cy.get('[data-cy=submit]').click();
});

// Use in test
cy.login('user@test.com', 'password');

