// .babelrc
{
  "presets": ["@babel/preset-react"],
  "plugins": [
    ["@emotion/babel-plugin", {
      "sourceMap": true,
      "autoLabel": "dev-only",
      "labelFormat": "[local]"
    }]
  ]
}

import '@emotion/react';

declare module '@emotion/react' {
  export interface Theme {
    colors: {
      primary: string;
      secondary: string;
    };
    spacing: {
      small: string;
      medium: string;
    };
  }
}

import createCache from '@emotion/cache';
import { CacheProvider } from '@emotion/react';

const cache = createCache({
  key: 'my-app',
  prepend: true,  // Styles at top of <head>
  stylisPlugins: []
});

<CacheProvider value={cache}>
  <App />
</CacheProvider>

import { css, cx } from '@emotion/css';

const base = css`
  padding: 10px;
`;

const active = css`
  background: blue;
`;

// Combine classes
<div className={cx(base, isActive && active)}>Content</div>

const buttonVariants = {
  primary: css`background: blue; color: white;`,
  secondary: css`background: gray; color: black;`,
  danger: css`background: red; color: white;`
};

const Button = styled.button<{ variant: keyof typeof buttonVariants }>`
  padding: 10px 20px;
  ${props => buttonVariants[props.variant]}
`;

import facepaint from 'facepaint';

const mq = facepaint([
  '@media(min-width: 420px)',
  '@media(min-width: 920px)',
  '@media(min-width: 1120px)'
]);

const responsive = css(mq({
  fontSize: ['14px', '16px', '18px', '20px'],
  padding: ['10px', '20px', '30px', '40px']
}));

import { serializeStyles } from '@emotion/serialize';

const serialized = serializeStyles([css`color: red;`]);
console.log(serialized.name);   // Hash
console.log(serialized.styles); // CSS string

import isPropValid from '@emotion/is-prop-valid';
import styled from '@emotion/styled';

const Box = styled('div', {
  shouldForwardProp: (prop) => 
    isPropValid(prop) && prop !== 'color'
})<{ color: string }>`
  background: ${props => props.color};
`;

import { matchers } from '@emotion/jest';
expect.extend(matchers);

test('Button has correct styles', () => {
  const { container } = render(<Button primary />);
  expect(container.firstChild).toHaveStyleRule(
    'background', 'blue'
  );
});

