skills.pattern:
fastify.route({
  method: 'GET',
  url: '/example',
  schema: { ... },
  handler: async (request, reply) => { ... }
});

const schema = {
  body: { type: 'object', properties: { name: { type: 'string' } }, required: ['name'] },
  response: { 200: { type: 'object', properties: { id: { type: 'number' } } } }
};

async function myPlugin(fastify, options) {
  fastify.decorate('utility', () => 'helper');
}
fastify.register(myPlugin);

fastify.decorate('db', dbConnection);
fastify.decorateRequest('user', null);
fastify.decorateReply('sendError', function(msg) { this.send({ error: msg }) });

fastify.setErrorHandler((error, request, reply) => {
  reply.status(500).send({ error: error.message });
});

fastify.setNotFoundHandler((request, reply) => {
  reply.status(404).send({ error: 'Not found' });
});

reply.send({ data });
reply.code(201).send();
reply.header('X-Custom', 'value');
reply.type('text/html');

fastify.get('/async', async (request) => {
  const result = await doAsync();
  return result; // auto-serialized
});
