// CDN
<script src="https://cdnjs.cloudflare.com/ajax/libs/gsap/3.12.2/gsap.min.js"></script>

import gsap from 'gsap';

gsap.to('.box', {
  x: 100,
  y: 50,
  rotation: 180,
  duration: 1
});

gsap.from('.box', {
  opacity: 0,
  y: -50,
  duration: 1
});

gsap.fromTo('.box',
  { opacity: 0, y: -50 },   // From
  { opacity: 1, y: 0, duration: 1 }  // To
);

gsap.set('.box', {
  x: 100,
  opacity: 0.5
});

gsap.to('.box', {
  x: 100,
  y: 50,
  rotation: 360,
  scale: 1.5,
  opacity: 0.5,
  backgroundColor: '#ff0000',
  borderRadius: '50%',
  transformOrigin: 'center'
});

gsap.to('.box', {
  x: 100,
  ease: 'power2.out',  // power1-4, in/out/inOut
  // ease: 'bounce.out',
  // ease: 'elastic.out(1, 0.3)',
  // ease: 'back.out(1.7)',
  // ease: 'steps(12)',
  duration: 1
});

gsap.to('.box', {
  y: 100,
  stagger: 0.1,  // Delay between each
  // stagger: { each: 0.1, from: 'center' }
  // stagger: { amount: 1, grid: [3, 3], from: 'center' }
  duration: 1
});

gsap.to('.box', {
  rotation: 360,
  repeat: -1,      // Infinite
  repeatDelay: 0.5,
  yoyo: true,      // Reverse on repeat
  duration: 1
});

const tl = gsap.timeline();

tl.to('.box1', { x: 100, duration: 1 })
  .to('.box2', { y: 100, duration: 1 })
  .to('.box3', { opacity: 0, duration: 1 });

gsap.to('.box', {
  x: 100,
  onStart: () => console.log('Start'),
  onUpdate: () => console.log('Update'),
  onComplete: () => console.log('Complete'),
  onRepeat: () => console.log('Repeat')
});

const tween = gsap.to('.box', { x: 100, paused: true });

tween.play();
tween.pause();
tween.reverse();
tween.restart();
tween.progress(0.5);  // Jump to 50%
tween.seek(1);        // Jump to 1 second
tween.kill();         // Remove

import { ScrollTrigger } from 'gsap/ScrollTrigger';
gsap.registerPlugin(ScrollTrigger);

gsap.to('.box', {
  scrollTrigger: {
    trigger: '.box',
    start: 'top center',
    end: 'bottom center',
    scrub: true,
    markers: true  // Debug
  },
  x: 500
});

const tl = gsap.timeline({
  scrollTrigger: {
    trigger: '.container',
    start: 'top top',
    end: '+=3000',
    scrub: 1,
    pin: true
  }
});

tl.to('.box1', { x: 100 })
  .to('.box2', { y: 100 });

ScrollTrigger.create({
  trigger: '.section',
  start: 'top top',
  end: '+=500',
  pin: true,
  pinSpacing: true
});

import { Draggable } from 'gsap/Draggable';
gsap.registerPlugin(Draggable);

Draggable.create('.box', {
  type: 'x,y',
  bounds: '.container',
  inertia: true,
  onDrag: () => console.log('Dragging')
});

import { Flip } from 'gsap/Flip';
gsap.registerPlugin(Flip);

const state = Flip.getState('.box');
// Change layout
element.classList.toggle('active');
Flip.from(state, { duration: 1, ease: 'power2.out' });

import { MotionPathPlugin } from 'gsap/MotionPathPlugin';
gsap.registerPlugin(MotionPathPlugin);

gsap.to('.box', {
  motionPath: {
    path: '#path',
    align: '#path',
    alignOrigin: [0.5, 0.5],
    autoRotate: true
  },
  duration: 5
});

import { SplitText } from 'gsap/SplitText';  // Club

const split = new SplitText('.text', { type: 'chars, words' });

gsap.from(split.chars, {
  opacity: 0,
  y: 50,
  stagger: 0.02
});

import { MorphSVGPlugin } from 'gsap/MorphSVGPlugin';  // Club

gsap.to('#circle', {
  morphSVG: '#square',
  duration: 1
});

import { DrawSVGPlugin } from 'gsap/DrawSVGPlugin';  // Club

gsap.from('path', {
  drawSVG: 0,  // or '0%', '50% 50%'
  duration: 2
});

import { useGSAP } from '@gsap/react';

function Component() {
  const container = useRef();

  useGSAP(() => {
    gsap.to('.box', { x: 100 });
  }, { scope: container });

  return <div ref={container}><div className="box" /></div>;
}

useEffect(() => {
  const ctx = gsap.context(() => {
    gsap.to('.box', { x: 100 });
  }, containerRef);

  return () => ctx.revert();  // Cleanup
}, []);

gsap.defaults({
  ease: 'power2.out',
  duration: 1
});

// Timeline defaults
const tl = gsap.timeline({
  defaults: { duration: 0.5, ease: 'power2.out' }
});

const xTo = gsap.quickTo('.box', 'x', { duration: 0.4 });
const yTo = gsap.quickTo('.box', 'y', { duration: 0.4 });

window.addEventListener('mousemove', (e) => {
  xTo(e.clientX);
  yTo(e.clientY);
});

const mm = gsap.matchMedia();

mm.add('(min-width: 768px)', () => {
  gsap.to('.box', { x: 200 });
});

mm.add('(max-width: 767px)', () => {
  gsap.to('.box', { y: 100 });
});

gsap.registerEffect({
  name: 'fade',
  effect: (targets, config) => {
    return gsap.to(targets, {
      opacity: 0,
      duration: config.duration
    });
  },
  defaults: { duration: 1 }
});

gsap.effects.fade('.box');

// 1. Use timelines for complex sequences
// 2. Register plugins once
// 3. Use context for React cleanup
// 4. Use quickTo for frequent updates
// 5. Kill tweens when unmounting
