const Koa = require('koa');
const app = new Koa();
app.listen(3000);

app.use(async (ctx, next) => {
  console.log('Before');
  await next();
  console.log('After');
});

app.use(async ctx => {
  ctx.body = 'Hello';
  ctx.status = 200;
  ctx.type = 'text/html';
});

ctx.response.body = data;
ctx.response.status = 201;
ctx.response.set('X-Custom', 'value');

const Router = require('@koa/router');
const router = new Router();
router.get('/', ctx => { ctx.body = 'Home' });
app.use(router.routes());

router.get('/users/:id', ctx => {
  ctx.body = { id: ctx.params.id }
});

const users = new Router();
users.get('/', handler);
router.use('/users', users.routes());

const bodyParser = require('koa-bodyparser');
app.use(bodyParser());
// ctx.request.body available

app.use(async (ctx, next) => {
  try { await next() }
  catch (err) {
    ctx.status = err.status || 500;
    ctx.body = { error: err.message };
  }
});

ctx.throw(400, 'Bad Request');
ctx.throw(404); // Not Found
ctx.assert(ctx.state.user, 401, 'Unauthorized');

const session = require('koa-session');
app.keys = ['secret'];
app.use(session(app));
ctx.session.userId = 1;

const views = require('koa-views');
app.use(views('./views', { extension: 'ejs' }));
await ctx.render('index', { title: 'Home' });

const multer = require('@koa/multer');
const upload = multer({ dest: 'uploads/' });
router.post('/upload', upload.single('file'), ctx => { ctx.body = ctx.file });

const compose = require('koa-compose');
const all = compose([middleware1, middleware2]);
app.use(all);

// In auth middleware
ctx.state.user = user;
// In route handler
const { user } = ctx.state;
