import pino from 'pino';

const logger = pino();

logger.info('Hello, Pino!');
logger.error({ err: new Error('Oops') }, 'Error occurred');

// trace: 10, debug: 20, info: 30, warn: 40, error: 50, fatal: 60

logger.trace('Trace message');
logger.debug('Debug message');
logger.info('Info message');
logger.warn('Warning message');
logger.error('Error message');
logger.fatal('Fatal message');

const logger = pino({
  level: 'info',
  name: 'my-app',
  base: { pid: false },  // Exclude pid
  timestamp: pino.stdTimeFunctions.isoTime
});

// Development
const logger = pino({
  transport: {
    target: 'pino-pretty',
    options: {
      colorize: true,
      translateTime: 'SYS:standard',
      ignore: 'pid,hostname'
    }
  }
});

const logger = pino();

const childLogger = logger.child({ requestId: 'abc123' });
childLogger.info('Processing request');
// Output includes requestId in all logs

logger.info({ user: { id: 123, name: 'John' } }, 'User logged in');

// Error objects
logger.error({ err: error }, 'Request failed');

// Merged object
logger.info({ req, res }, 'Request completed');

const logger = pino({
  redact: ['password', 'user.password', 'headers.authorization']
});

logger.info({ password: 'secret123' });
// Output: { password: '[Redacted]' }

const logger = pino({
  transport: {
    targets: [
      { target: 'pino-pretty', level: 'info' },
      { target: 'pino/file', options: { destination: './app.log' } }
    ]
  }
});

import pino from 'pino';
import fs from 'fs';

const dest = pino.destination('./app.log');
const logger = pino(dest);

// Or with sync writes
const syncDest = pino.destination({ dest: './app.log', sync: false });

// Use pino with rotating-file-stream
import { createStream } from 'rotating-file-stream';

const stream = createStream('app.log', {
  size: '10M',
  interval: '1d',
  path: './logs'
});

const logger = pino(stream);

import pinoHttp from 'pino-http';

const httpLogger = pinoHttp({
  logger: pino(),
  autoLogging: true,
  customLogLevel: (req, res, err) => {
    if (res.statusCode >= 500) return 'error';
    if (res.statusCode >= 400) return 'warn';
    return 'info';
  }
});

app.use(httpLogger);

import Fastify from 'fastify';

const fastify = Fastify({
  logger: {
    level: 'info',
    transport: {
      target: 'pino-pretty'
    }
  }
});

fastify.log.info('Server starting');

const dest = pino.destination({
  dest: './app.log',
  sync: false,        // Async writes
  minLength: 4096     // Buffer size
});

const logger = pino(dest);

// Flush on exit
process.on('beforeExit', () => dest.flushSync());

const logger = pino({
  serializers: {
    req: (req) => ({
      method: req.method,
      url: req.url,
      headers: req.headers
    }),
    err: pino.stdSerializers.err,
    user: (user) => ({ id: user.id, email: user.email })
  }
});

const logger = pino({
  hooks: {
    logMethod(inputArgs, method) {
      if (inputArgs.length >= 2) {
        const arg1 = inputArgs.shift();
        const arg2 = inputArgs.shift();
        return method.apply(this, [arg1, arg2, ...inputArgs]);
      }
      return method.apply(this, inputArgs);
    }
  }
});

import pino from 'pino';

const streams = [
  { stream: process.stdout },
  { level: 'error', stream: pino.destination('./errors.log') },
  { level: 'fatal', stream: pino.destination('./fatal.log') }
];

const logger = pino({ level: 'debug' }, pino.multistream(streams));

import pinoElastic from 'pino-elasticsearch';

const streamToElastic = pinoElastic({
  index: 'logs',
  node: 'http://localhost:9200',
  esVersion: 8,
  flushBytes: 1000
});

const logger = pino({ level: 'info' }, streamToElastic);

import pino from 'pino';
import pinoLoki from 'pino-loki';

const logger = pino({
  transport: {
    target: 'pino-loki',
    options: {
      host: 'http://localhost:3100',
      labels: { app: 'my-app' }
    }
  }
});

const logger = pino({
  customLevels: {
    http: 35,
    verbose: 25
  },
  useOnlyCustomLevels: false
});

logger.http('HTTP request');
logger.verbose('Verbose message');

const logger = pino({
  base: {
    app: 'my-app',
    version: '1.0.0',
    environment: process.env.NODE_ENV
  }
});

// All logs include app, version, environment

const logger = pino({
  timestamp: pino.stdTimeFunctions.isoTime,
  // or: pino.stdTimeFunctions.epochTime
  // or: pino.stdTimeFunctions.unixTime
  // or custom: () => `,'time':"${new Date().toISOString()}"`
});

const logger = pino({
  mixin() {
    return {
      correlationId: getCorrelationId(),
      requestId: getRequestId()
    };
  }
});

import pino from 'pino';

const logger = pino({
  browser: {
    asObject: true,
    transmit: {
      level: 'info',
      send: (level, logEvent) => {
        fetch('/api/logs', {
          method: 'POST',
          body: JSON.stringify(logEvent)
        });
      }
    }
  }
});

const logger = pino({ level: 'silent' });

// Or dynamically
logger.level = 'silent';

import pino, { Logger } from 'pino';

const logger: Logger = pino({
  level: 'info'
});

logger.info({ userId: number }, 'Message');

import pinoHttp from 'pino-http';

const httpLogger = pinoHttp({
  genReqId: (req) => req.headers['x-request-id'] || uuid(),
  customProps: (req) => ({ userId: req.user?.id }),
  customSuccessMessage: (req, res) => `${req.method} ${req.url}`,
  customErrorMessage: (req, res, err) => `Error: ${err.message}`
});

const logger = pino({
  transport: {
    target: 'pino/file',
    options: { destination: './app.log' },
    worker: {
      autoEnd: true
    }
  }
});

import pino from 'pino';

const testLogger = pino({ level: 'silent' });

// Or capture logs
const logs = [];
const logger = pino({}, {
  write: (msg) => logs.push(JSON.parse(msg))
});

