datasource db {
  provider = "postgresql"
  url = env("DATABASE_URL")
}

generator client {
  provider = "prisma-client-js"
}

model Post {
  id       Int  @id @default(autoincrement())
  author   User @relation(fields: [authorId], references: [id])
  authorId Int
}

model Post {
  tags Tag[]
}
model Tag {
  posts Post[]
}

const user = await prisma.user.create({
  data: { email: 'john@example.com', name: 'John' }
});

const users = await prisma.user.createMany({
  data: [{ email: 'a@a.com' }, { email: 'b@b.com' }],
  skipDuplicates: true
});

const users = await prisma.user.findMany({
  where: { active: true },
  orderBy: { createdAt: 'desc' },
  take: 10,
  skip: 0
});

const user = await prisma.user.update({
  where: { id: 1 },
  data: { name: 'Jane' }
});

const user = await prisma.user.upsert({
  where: { email: 'john@example.com' },
  create: { email: 'john@example.com', name: 'John' },
  update: { name: 'John Updated' }
});

where: {
  AND: [{ email: { contains: '@' } }, { age: { gte: 18 } }],
  OR: [{ status: 'ACTIVE' }, { role: 'ADMIN' }],
  NOT: { deleted: true }
}

const user = await prisma.user.findUnique({
  where: { id: 1 },
  include: { posts: true, profile: true }
});

const user = await prisma.user.findUnique({
  where: { id: 1 },
  select: { id: true, email: true, posts: { select: { title: true } } }
});

const stats = await prisma.user.aggregate({
  _count: true,
  _avg: { age: true },
  _sum: { salary: true }
});

const grouped = await prisma.user.groupBy({
  by: ['department'],
  _count: { _all: true },
  _avg: { salary: true }
});

const [user, post] = await prisma.$transaction([
  prisma.user.create({ data: { email: 'a@a.com' } }),
  prisma.post.create({ data: { title: 'Hello' } })
]);

await prisma.$transaction(async (tx) => {
  const user = await tx.user.create({ ... });
  await tx.post.create({ data: { authorId: user.id, ... } });
});

prisma.$use(async (params, next) => {
  console.log(params.model, params.action);
  return next(params);
});
