import { component$ } from '@builder.io/qwik';

export default component$(() => {
  return <h1>Hello Qwik!</h1>;
});

import { component$, useSignal } from '@builder.io/qwik';

export default component$(() => {
  const count = useSignal(0);
  return <button onClick$={() => count.value++}>{count.value}</button>;
});

import { useStore } from '@builder.io/qwik';

const state = useStore({ count: 0, user: { name: 'John' } });

<p>{state.user.name}: {state.count}</p>

import { useTask$ } from '@builder.io/qwik';

useTask$(({ track }) => {
  track(() => count.value);
  console.log('Count:', count.value);
});

import { useVisibleTask$ } from '@builder.io/qwik';

useVisibleTask$(() => {
  // Runs in browser when visible
  window.scrollTo(0, 0);
});

import { useResource$, Resource } from '@builder.io/qwik';

const userResource = useResource$(async ({ track }) => {
  track(() => userId.value);
  return (await fetch(`/api/users/${userId.value}`)).json();
});

<Resource
  value={userResource}
  onPending={() => <p>Loading...</p>}
  onResolved={(user) => <p>{user.name}</p>}
/>

import { Slot } from '@builder.io/qwik';

export const Card = component$(() => (
  <div class="card">
    <Slot name="header" />
    <Slot />  {/* Default */}
  </div>
));

<Card>
  <span q:slot="header">Title</span>
  <p>Content</p>
</Card>

import { createContextId, useContextProvider, useContext } from '@builder.io/qwik';

const ThemeCtx = createContextId<{ theme: string }>('theme');

useContextProvider(ThemeCtx, { theme: 'dark' });
const ctx = useContext(ThemeCtx);

// routes/index.tsx -> /
// routes/about/index.tsx -> /about
// routes/blog/[slug]/index.tsx -> /blog/:slug
// routes/layout.tsx -> wrapper

import { routeLoader$ } from '@builder.io/qwik-city';

export const useUserData = routeLoader$(async () => {
  return (await fetch('/api/user')).json();
});

export default component$(() => {
  const user = useUserData();
  return <p>{user.value.name}</p>;
});

import { routeAction$, Form } from '@builder.io/qwik-city';

export const useCreate = routeAction$(async (data) => {
  await db.create(data);
  return { success: true };
});

export default component$(() => {
  const action = useCreate();
  return (
    <Form action={action}>
      <input name="title" />
      <button>{action.isRunning ? 'Saving...' : 'Save'}</button>
    </Form>
  );
});

