import { useForm } from 'react-hook-form';

function Form() {
  const { register, handleSubmit, formState: { errors } } = useForm();

  const onSubmit = (data) => console.log(data);

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <input {...register('name', { required: true })} />
      {errors.name && <span>Required</span>}
      <button type="submit">Submit</button>
    </form>
  );
}

register('email', {
  required: 'Email is required',
  pattern: {
    value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
    message: 'Invalid email'
  }
})

register('age', {
  min: { value: 18, message: 'Must be 18+' },
  max: { value: 100, message: 'Max 100' }
})

register('password', {
  minLength: { value: 8, message: 'Min 8 chars' }
})

const { formState: { errors } } = useForm();

<input {...register('email', { required: 'Email required' })} />
{errors.email && <p>{errors.email.message}</p>}

// Or with type
{errors.email?.type === 'required' && <p>Required</p>}
{errors.email?.type === 'pattern' && <p>Invalid format</p>}

const { register } = useForm({
  defaultValues: {
    name: 'John',
    email: 'john@example.com',
    preferences: {
      newsletter: true
    }
  }
});

// Async default values
const { register } = useForm({
  defaultValues: async () => {
    const user = await fetchUser();
    return user;
  }
});

const { watch } = useForm();

// Watch single field
const name = watch('name');

// Watch multiple fields
const [name, email] = watch(['name', 'email']);

// Watch all fields
const allFields = watch();

// Watch with callback
useEffect(() => {
  const subscription = watch((data) => console.log(data));
  return () => subscription.unsubscribe();
}, [watch]);

const { setValue, reset } = useForm();

// Set single field
setValue('name', 'John');
setValue('name', 'John', { shouldValidate: true });
setValue('name', 'John', { shouldDirty: true });

// Reset form
reset();  // Reset to defaults
reset({ name: 'Jane' });  // Reset with new values

const { getValues, getFieldState } = useForm();

// Get all values
const data = getValues();

// Get single field
const name = getValues('name');

// Get multiple fields
const [name, email] = getValues(['name', 'email']);

// Get field state
const { isDirty, isTouched, error } = getFieldState('name');

