import { createClient } from 'redis';
const client = createClient({ url: 'redis://localhost:6379' });
await client.connect();

await client.incr('counter');
await client.incrBy('counter', 5);
await client.decr('counter');

await client.hSet('user:1', { name: 'John', age: '30' });
const name = await client.hGet('user:1', 'name');
const user = await client.hGetAll('user:1');

await client.sAdd('tags', ['redis', 'cache', 'database']);
const tags = await client.sMembers('tags');
const isMember = await client.sIsMember('tags', 'redis');

const union = await client.sUnion(['set1', 'set2']);
const inter = await client.sInter(['set1', 'set2']);
const diff = await client.sDiff(['set1', 'set2']);

await client.expire('key', 3600); // 1 hour
const ttl = await client.ttl('key');
await client.persist('key'); // remove expiration

await client.del('key');
const exists = await client.exists('key');
const keys = await client.keys('user:*');
await client.rename('old', 'new');

const subscriber = client.duplicate();
await subscriber.connect();
await subscriber.subscribe('channel', (message) => { console.log(message) });

const multi = client.multi();
multi.set('key1', 'val1');
multi.incr('counter');
const results = await multi.exec();

const pipeline = client.pipeline();
pipeline.get('key1');
pipeline.get('key2');
const results = await pipeline.exec();

