import { useRouteError, isRouteErrorResponse } from '@remix-run/react';

export function ErrorBoundary() {
  const error = useRouteError();

  if (isRouteErrorResponse(error)) {
    return <div>{error.status} - {error.statusText}</div>;
  }

  return <div>Something went wrong</div>;
}

import type { MetaFunction } from '@remix-run/node';

export const meta: MetaFunction = () => {
  return [
    { title: 'My Page' },
    { name: 'description', content: 'Page description' }
  ];
};

// Dynamic meta
export const meta: MetaFunction<typeof loader> = ({ data }) => {
  return [{ title: data.post.title }];
};

import type { LinksFunction } from '@remix-run/node';
import styles from '~/styles/app.css';

export const links: LinksFunction = () => [
  { rel: 'stylesheet', href: styles },
  { rel: 'icon', href: '/favicon.ico' }
];

import { useFetcher } from '@remix-run/react';

export default function LikeButton({ postId }) {
  const fetcher = useFetcher();
  const isLiking = fetcher.state !== 'idle';

  return (
    <fetcher.Form method="post" action="/like">
      <input type="hidden" name="postId" value={postId} />
      <button disabled={isLiking}>Like</button>
    </fetcher.Form>
  );
}

export default function Todo({ todo }) {
  const fetcher = useFetcher();

  // Use optimistic value if submitting
  const isCompleted = fetcher.formData
    ? fetcher.formData.get('completed') === 'true'
    : todo.completed;

  return (
    <fetcher.Form method="post">
      <input type="hidden" name="completed" value={!isCompleted} />
      <span style={{ textDecoration: isCompleted ? 'line-through' : 'none' }}>
        {todo.title}
      </span>
    </fetcher.Form>
  );
}

import { useRevalidator } from '@remix-run/react';

export default function Dashboard() {
  const revalidator = useRevalidator();

  return (
    <button
      onClick={() => revalidator.revalidate()}
      disabled={revalidator.state === 'loading'}
    >
      Refresh
    </button>
  );
}

import { createCookieSessionStorage } from '@remix-run/node';

export const sessionStorage = createCookieSessionStorage({
  cookie: {
    name: '_session',
    secrets: ['s3cr3t'],
    secure: true,
    httpOnly: true,
    sameSite: 'lax'
  }
});

export const { getSession, commitSession, destroySession } = sessionStorage;

export async function loader({ request }) {
  const session = await getSession(request.headers.get('Cookie'));
  const userId = session.get('userId');

  if (!userId) throw redirect('/login');

  const user = await db.user.findUnique({ where: { id: userId } });
  return json({ user });
}

import { redirect } from '@remix-run/node';

export async function loader({ request }) {
  const user = await getUser(request);
  if (!user) return redirect('/login');
  return json({ user });
}

export async function action({ request }) {
