import { defer } from '@remix-run/node';
import { Await, useLoaderData } from '@remix-run/react';
import { Suspense } from 'react';

export async function loader() {
  return defer({
    critical: await getCritical(),
    lazy: getLazyData()  // Promise, not awaited
  });
}

export default function Page() {
  const { critical, lazy } = useLoaderData<typeof loader>();
  return (
    <Suspense fallback={<Loading />}>
      <Await resolve={lazy}>{(data) => <LazyContent data={data} />}</Await>
    </Suspense>
  );
}

import { Link } from '@remix-run/react';

<Link to="/about" prefetch="intent">About</Link>

// prefetch options:
