// Access in parent
import { useMatches } from '@remix-run/react';

const matches = useMatches();
const breadcrumbs = matches
  .filter(m => m.handle?.breadcrumb)
  .map(m => m.handle.breadcrumb());

export function shouldRevalidate({ currentUrl, nextUrl, formMethod }) {
  // Don't revalidate on search param changes
  if (currentUrl.pathname === nextUrl.pathname) {
    return false;
  }
  return true;
}

import invariant from 'tiny-invariant';

export async function action({ request, params }) {
  invariant(params.id, 'Missing id');

  const formData = await request.formData();
  const title = formData.get('title');
  invariant(typeof title === 'string', 'Title required');

  return createPost({ id: params.id, title });
}

