// .storybook/main.ts
import type { StorybookConfig } from '@storybook/react-vite';

const config: StorybookConfig = {
  stories: ['../src/**/*.stories.@(js|jsx|ts|tsx|mdx)'],
  addons: [
    '@storybook/addon-links',
    '@storybook/addon-essentials',
    '@storybook/addon-interactions'
  ],
  framework: '@storybook/react-vite'
};

export default config;

import type { Meta, StoryObj } from '@storybook/react';
import { Button } from './Button';

const meta: Meta<typeof Button> = {
  title: 'Components/Button',
  component: Button
};

export default meta;
type Story = StoryObj<typeof Button>;

export const Primary: Story = {
  args: {
    primary: true,
    label: 'Button'
  }
};

export const Large: Story = {
  args: {
    size: 'large',
    label: 'Large Button'
  }
};

export const Small: Story = {
  args: {
    size: 'small',
    label: 'Small Button'
  }
};

const meta: Meta<typeof Button> = {
  component: Button,
  argTypes: {
    backgroundColor: { control: 'color' },
    size: {
      control: 'select',
      options: ['small', 'medium', 'large']
    },
    onClick: { action: 'clicked' },
    disabled: { control: 'boolean' }
  }
};

export const WithIcon: Story = {
  args: {
    label: 'With Icon'
  },
  render: (args) => (
    <Button {...args}>
      <Icon name="star" />
      {args.label}
    </Button>
  )
};

// Story level
export const Centered: Story = {
  decorators: [
    (Story) => (
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <Story />
      </div>
    )
  ]
};

// Meta level
const meta: Meta<typeof Button> = {
  decorators: [(Story) => <ThemeProvider><Story /></ThemeProvider>]
};

export const Mobile: Story = {
  parameters: {
    viewport: {
      defaultViewport: 'iphone6'
    },
    backgrounds: {
      default: 'dark'
    },
    layout: 'centered'  // or 'fullscreen', 'padded'
  }
};

import { within, userEvent } from '@storybook/testing-library';
import { expect } from '@storybook/jest';

export const Filled: Story = {
  play: async ({ canvasElement }) => {
    const canvas = within(canvasElement);

    const input = canvas.getByRole('textbox');
    await userEvent.type(input, 'Hello');

    expect(input).toHaveValue('Hello');
  }
};

// Component with JSDoc
/**
 * Primary UI component for user interaction
 */
export const Button = ({ primary, label }: ButtonProps) => {
  // ...
};

// In meta
const meta: Meta<typeof Button> = {
  tags: ['autodocs']  // Auto-generate docs
};

// Auto-runs accessibility checks
export const Accessible: Story = {
  parameters: {
    a11y: {
      config: {
        rules: [
          { id: 'color-contrast', enabled: true }
        ]
      }
    }
  }
};

// .storybook/preview.ts
export const parameters = {
  viewport: {
    viewports: {
      mobile: {
        name: 'Mobile',
        styles: { width: '375px', height: '667px' }
      },
      tablet: {
        name: 'Tablet',
        styles: { width: '768px', height: '1024px' }
      }
    },
    defaultViewport: 'mobile'
  }
};

export const parameters = {
  backgrounds: {
    default: 'light',
    values: [
      { name: 'light', value: '#ffffff' },
      { name: 'dark', value: '#333333' },
      { name: 'twitter', value: '#00aced' }
    ]
  }
};

import { action } from '@storybook/addon-actions';

export const WithActions: Story = {
  args: {
    onClick: action('clicked'),
    onHover: action('hovered')
  }
};

// Or in argTypes
argTypes: {
  onClick: { action: 'clicked' }
}

// .storybook/preview.ts
export const globalTypes = {
  theme: {
    name: 'Theme',
    description: 'Global theme',
    defaultValue: 'light',
    toolbar: {
      icon: 'circlehollow',
      items: ['light', 'dark'],
      showName: true
    }
  }
};

// Access in decorator
const withTheme = (Story, context) => (
  <ThemeProvider theme={context.globals.theme}>
    <Story />
  </ThemeProvider>
);

export const WithData: Story = {
  loaders: [
    async () => ({
      users: await fetch('/api/users').then(r => r.json())
    })
  ],
  render: (args, { loaded: { users } }) => (
    <UserList users={users} />
  )
};

import { rest } from 'msw';
import { initialize, mswLoader } from 'msw-storybook-addon';

initialize();

export const parameters = {
  msw: {
    handlers: [
      rest.get('/api/users', (req, res, ctx) => {
        return res(ctx.json([{ id: 1, name: 'John' }]));
      })
    ]
  }
};

export const loaders = [mswLoader];

import { composeStories } from '@storybook/react';
import * as stories from './Button.stories';

const { Primary, Secondary } = composeStories(stories);

test('renders primary button', () => {
  render(<Primary />);
  expect(screen.getByRole('button')).toHaveClass('primary');
});

test('runs play function', async () => {
  const { container } = render(<Primary />);
  await Primary.play({ canvasElement: container });
});

npm install -D chromatic

