npm install @tailwindcss/forms @tailwindcss/typography @tailwindcss/aspect-ratio

// tailwind.config.js
module.exports = {
  plugins: [
    require('@tailwindcss/forms'),
    require('@tailwindcss/typography'),
    require('@tailwindcss/aspect-ratio')
  ]
};

@layer components {
  .btn {
    @apply px-4 py-2 rounded-lg font-medium;
  }

  .btn-primary {
    @apply bg-blue-500 text-white hover:bg-blue-600;
  }
}

<button class="btn btn-primary">Button</button>

.card {
  @apply bg-white rounded-lg shadow-md p-6;
}

.card-title {
  @apply text-xl font-bold text-gray-900 mb-2;
}

.card-content {
  @apply text-gray-600 leading-relaxed;
}

// JIT is default in v3+
// Enables arbitrary values and variants

<div class="hover:[mask-type:alpha]">
  JIT-generated class
</div>

// tailwind.config.js
module.exports = {
  safelist: [
    'bg-red-500',
    'bg-green-500',
    'bg-blue-500',
    {
      pattern: /bg-(red|green|blue)-(100|500)/
    }
  ]
};

// tailwind.config.js
module.exports = {
  important: true,  // All utilities !important
  // or important: '#app' // Selector strategy
};

// Or per-class
<div class="!p-4">Force padding</div>

