skills.pattern:
import { defineConfig } from 'vite';
import react from '@vitejs/plugin-react';

export default defineConfig({
  plugins: [react()],
  server: { port: 3000 }
});

resolve: {
  alias: {
    '@': path.resolve(__dirname, './src'),
    '@components': './src/components'
  }
}

css: {
  preprocessorOptions: {
    scss: {
      additionalData: '@import "@/styles/variables.scss";'
    }
  },
  modules: { localsConvention: 'camelCase' }
}

server: {
  proxy: {
    '/api': {
      target: 'http://localhost:5000',
      changeOrigin: true,
      rewrite: (path) => path.replace(/^\/api/, '')
    }
  }
}

server: {
  hmr: {
    overlay: true,
    host: 'localhost'
  }
}

build: {
  outDir: 'dist',
  sourcemap: true,
  minify: 'esbuild',
  target: 'esnext'
}

build: {
  rollupOptions: {
    output: {
      manualChunks: {
        vendor: ['react', 'react-dom'],
        utils: ['lodash', 'date-fns']
      }
    }
  }
}

import imgUrl from './img.png';
import content from './file.txt?raw';
<img src={imgUrl} />
<img src="/public/logo.png" />

function myPlugin() {
  return {
    name: 'my-plugin',
    transform(code, id) {
      if (id.endsWith('.custom')) {
        return { code: transformCode(code) };
      }
    }
  };
}

build: {
  ssr: true,
  rollupOptions: { input: 'src/entry-server.tsx' }
}

build: {
  lib: {
    entry: './src/index.ts',
    name: 'MyLib',
    fileName: (format) => `my-lib.${format}.js`
  },
  rollupOptions: {
    external: ['react'],
    output: { globals: { react: 'React' } }
  }
}

import { VitePWA } from 'vite-plugin-pwa';

plugins: [VitePWA({
  registerType: 'autoUpdate',
  manifest: { name: 'My App' }
})]

import basicSsl from '@vitejs/plugin-basic-ssl';

plugins: [basicSsl()],
server: { https: true }

define: {
  __APP_VERSION__: JSON.stringify('1.0.0')
}

// Usage: console.log(__APP_VERSION__);

optimizeDeps: {
  include: ['lodash-es'],
  exclude: ['your-local-package']
}

preview: {
  port: 4173,
  open: true
}

// .env.staging
VITE_API=https://staging.api.com

vite build --mode staging

// postcss.config.js
export default {
  plugins: {
    tailwindcss: {},
    autoprefixer: {}
  }
};

import init, { greet } from './hello.wasm?init';
await init();
greet('World');
